import React, { useEffect, useState } from "react";
import ReactPlayer from 'react-player';
import Modal from 'react-responsive-modal';
import axios from "axios";

function Player({ children, media_type, id }) {
  const [open, setOpen] = useState(false);
  const [content, setContent] = useState();
  const [video, setVideo] = useState();

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const fetchData = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/${media_type}/${id}?api_key=ba8f4caa4e6ebec49f8c6b8ba603ed04&language=en-US`
    );

    setContent(data);
    // console.log(data);
  };

  const fetchVideo = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/${media_type}/${id}/videos?api_key=ba8f4caa4e6ebec49f8c6b8ba603ed04&language=en-US`
    );

    setVideo(data.results[0]?.key);
  };

  useEffect(() => {
    fetchData();
    fetchVideo();
    // eslint-disable-next-line
  }, []);

  return (
    <>
        <div
        className="media"
        style={{ cursor: "pointer" }}
        color="inherit"
        onClick={handleOpen}
        >
        {children}
        </div>
        <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className="modal"
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
            timeout: 500,
        }}
        >
            <Fade in={open}>
                {content && (
                <div className="paper">
                    <div className="ContentModal">
                    <img
                        src={
                        content.poster_path
                            ? `${img_500}/${content.poster_path}`
                            : unavailable
                        }
                        alt={content.name || content.title}
                        className="ContentModal__portrait"
                    />
                    <img
                        src={
                        content.backdrop_path
                            ? `${img_500}/${content.backdrop_path}`
                            : unavailableLandscape
                        }
                        alt={content.name || content.title}
                        className="ContentModal__landscape"
                    />
                    <div className="ContentModal__about">
                        <span className="ContentModal__title">
                        {content.name || content.title} (
                        {(
                            content.first_air_date ||
                            content.release_date ||
                            "-----"
                        ).substring(0, 4)}
                        )
                        </span>
                        {content.tagline && (
                        <i className="tagline">{content.tagline}</i>
                        )}

                        <span className="ContentModal__description">
                        {content.overview}
                        </span>

                        <div>
                        <Carousel id={id} media_type={media_type} />
                        </div>

                        <Button
                        variant="contained"
                        startIcon={<YouTubeIcon />}
                        color="secondary"
                        target="__blank"
                        href={`https://www.youtube.com/watch?v=${video}`}
                        >
                        Watch the Trailer
                        </Button>
                    </div>
                    </div>
                </div>
                )}
            </Fade>
        </Modal>
    </>
  );

}

export default Player
