import React, { useEffect, useState } from "react";
import Modal from 'react-modal';



function Login() {
  const [open, setOpen] = useState(false);

  function openModal() {
    setOpen(true);
  }

  function closeModal() {
    setOpen(false);
  }

  return (
    <>
        <div
        color="inherit"
        style={{ cursor: "pointer" }}
        onClick={openModal}
        >
            Login
        </div>
        <Modal
        isOpen={open}
        onRequestClose={closeModal}
        contentLabel="Modal"
        size="lg"
        >
            <h3>Login</h3>
        </Modal>
    </>
  );

}

export default Login
