import React, { useEffect, useState } from "react";
import ReactPlayer from 'react-player';
import Modal from 'react-modal';
import axios from "axios";

function Player({ children, media_type, id }) {
  const [open, setOpen] = useState(false);
  const [content, setContent] = useState();
  const [video, setVideo] = useState();

  function openModal() {
    setOpen(true);
  }

  function afterOpenModal() {
  }

  function closeModal() {
    setOpen(false);
  }

  const fetchData = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/${media_type}/${id}?api_key=ba8f4caa4e6ebec49f8c6b8ba603ed04&language=en-US`
    );

    setContent(data);
    // console.log(data);
  };

  const fetchVideo = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/${media_type}/${id}/videos?api_key=ba8f4caa4e6ebec49f8c6b8ba603ed04&language=en-US`
    );

    setVideo(data.results[0]?.key);
  };

  useEffect(() => {
    fetchData();
    fetchVideo();
    // eslint-disable-next-line
  }, []);

  return (
    <>
        <div
        className="media"
        style={{ cursor: "pointer" }}
        color="inherit"
        onClick={handleOpen}
        >
            {children}
            <button >Open Modal</button>
        </div>
        <Modal
        isOpen={open}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
        >
            <h2>Simple centered modal</h2>
        </Modal>
    </>
  );

}

export default Player
