import React, {Fragment, Component} from 'react'
import {AiFillPlayCircle} from 'react-icons/ai'
import NoImg from './NoImg.png'
import '../Styles/Videos.css'
import Player from "./Player";

function Movies({data}) {
    // const {toggle} = useContext(Container)

    const Images = "https://image.tmdb.org/t/p/w500";
    const state = {
        open: false
    };

    const onOpenModal = () => {
        state.open = !state.open
    };

  return (
    <Fragment>
        <div className='secondaryBgColor'>
        <div className='movies-container'>
        {data.map((movie) => {
            return(
            <Fragment>
                <div id='container' className='card-movies'>
                    <AiFillPlayCircle color='red' fontSize={40} id='playIcon'/>
                    <button onClick={() => onOpenModal()}>Play Video </button>
                    <Player open={state.open} toggleModal={onOpenModal} data={movie.id}  />
                    <img src={movie.poster_path ? `${Images}${movie.poster_path}` : NoImg } alt=''/>
                    <h3>{movie.title}</h3>
                    <h3>{movie.id}</h3>
                </div>
            </Fragment>
            )
        })}
        </div></div>
    </Fragment>
  )
}

export default Movies
