    const Images = "https://image.tmdb.org/t/p/w500";

  return (
    <Fragment>
        <div className='secondaryBgColor'>
        <div className='movies-container'>
        {data.map((movie) => {
            return(
            <Fragment>
                <ContentModal media_type={media_type} id={id}></ContentModal>
                <div id={movie.id} className='card-movies container'>
                    <AiFillPlayCircle color='red' fontSize={40} id='playIcon'/>
                    <img src={movie.poster_path ? `${Images}${movie.poster_path}` : NoImg } alt=''/>
                    <h3>{movie.title}</h3>
                </div>
            </Fragment>
            )
        })}
        </div></div>
    </Fragment>
  )
}

export default Movies
