import React, { useEffect, useState } from "react";
import Modal from 'react-modal';

const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      width: '50%',
      height: '60%',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
};

function Login() {
  const [open, setOpen] = useState(false);
  const [isSubmitting,handleSubmit] = useState(true);

  function openModal() {
    setOpen(true);
  }

  function closeModal() {
    setOpen(false);
  }

  return (
    <>
        <div
        color="inherit"
        style={{ cursor: "pointer" }}
        onClick={openModal}
        >
            Login
        </div>
        <Modal
        isOpen={open}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Modal"
        size="lg"
        >
            <h3>Login</h3>
            <form onSubmit={handleSubmit}>
                <div>Username :</div>
                <div>
                    <input type="text" name="username" id="username" />
                </div>
                <div>Password :</div>
                <div>
                    <input type="password" name="password" id="password" />
                </div>
                <div>
                    <input type="checkbox" name="permanent" id="permanent" /> permanent login
                </div>
                <input type="submit" value="Login" />
            </form>
        </Modal>
    </>
  );

}

export default Login
